function clearStim% This function clear all stimuli on the screen.global GRID_SIZE_PIX CUR_WINDOW SCREEN_RECT RED_TARGET APBG_INDEX     global TX TY T2X T2Y MONKEY_ID CONFIG NTARGET T_ANGLE_OFFSET FP_COLOR global CENTER_X CENTER_Y FP_SIZE_PIX% Fixation point% showFP;RED_TARGET = 1;xstart=SCREEN_RECT(3)/2-GRID_SIZE_PIX;ystart=SCREEN_RECT(4)/2-GRID_SIZE_PIX;SCREEN('FillRect', CUR_WINDOW, APBG_INDEX, [xstart ystart xstart+2*GRID_SIZE_PIX ystart+GRID_SIZE_PIX*2]);% show fp % Show FPScreen('FillOval',CUR_WINDOW,FP_COLOR,[CENTER_X-FP_SIZE_PIX/2 ...                                       CENTER_Y-FP_SIZE_PIX/2 ...                                       CENTER_X+FP_SIZE_PIX/2 ...                                       CENTER_Y+FP_SIZE_PIX/2]);                              % CONFIG = 1;% show targetNTARGET = 2;showTarget(NTARGET, TX, TY, T2X, T2Y, NaN, 1, CONFIG, MONKEY_ID,100,T_ANGLE_OFFSET,0)% wx = TX * PIX_PER_DEG /10 + CENTER_X;% wy = -TY * PIX_PER_DEG /10 + CENTER_Y;% wx2 = T2X * PIX_PER_DEG /10 + CENTER_X;% wy2 = -T2Y * PIX_PER_DEG /10 + CENTER_Y;% % Screen('FillOval',CUR_WINDOW,T1_COLOR,[wx-e wy-e wx+e wy+e]);% Screen('FillOval',CUR_WINDOW,T2_COLOR,[wx2-e wy2-e wx2+e wy2+e]);% Screen('Flip',CUR_WINDOW);return;