% nonfunction declareGlobals()% declares the global variables that are seen by all the service routines% These are either initialized in dotDefaults.m or sent from Rex.%	See setGlobalDefaults.m for more detailed explanations of each variable%% Dot Parameters sent from Rex are in units of 10ths of deg% Coherence is on 0..999 scale.% Dir is in degrees.% Vdist is in cm% History% ??? mns wrote it% 6/12/96  added the psychophysics section for dtdemo -- see below% 10/21/97 jig changed names to ALL_CAPS, as per established conventions% Targetsglobal TARGET_MATRIX% miscglobal WENT_FLAG				% send "went" via pcmsg after each commandglobal MONKEY_ID                % 1 for Eli, 2 for Joey% Brainard Screen stuffglobal CUR_WINDOW				% the window being drawn inglobal CUR_SCREEN				% the monitor to useglobal DOTS_CLUT% Monitor stuff.global MON_REFRESH				% refresh rate (in Hz)global PIX_PER_DEG				% based on distance between viewer and screenglobal SCREEN_RECT				% [left top right bottom]global CENTER_Xglobal CENTER_Y% Fixation point stuffglobal FP_SIZE_DEGglobal FP_SIZE_PIXglobal CUR_FP% colors and color look-up table stuff ("_INDEX" vars are indices into CLUT)global BG_COLOR		% background colorglobal T1_COLOR		% target colorglobal T2_COLORglobal FP_COLOR		% fixation point colorglobal FP2_COLORglobal STIM_COLOR	% stimulus colorglobal APFG_INDEX APBG_INDEX						% aperture color indicesglobal T_RED_INDEX T_GREEN_INDEX T_BLUE_INDEX		% target color indicesglobal T_YELLOW_INDEX T_WHITE_INDEX T_GRAY_INDEXglobal T1_INDEX T2_INDEX T3_INDEX T4_INDEX T5_INDEX global T6_INDEX T7_INDEX T8_INDEX T9_INDEXglobal DOT_RED_INDEX DOT_GREEN_INDEX 				% dots color indicesglobal DOT_BLUE_INDEX DOT_YELLOW_INDEXglobal DOT_WHITE_INDEX DOT_GRAY1_INDEX DOT_GRAY2_INDEXglobal CRED CGREEN CBLUE CYELLOW CWHITE CGRAY 		% colorsglobal CGRAY2 CBLACK% Stimuli parameters (770 specific)global NSTIM			% number of stimulus typesglobal GRID_SIZE_DEG	% size of gridglobal GRID_SIZE_PIX	global STIM_SIZE_DEG	% size of stim (measured as the side when it's a square)global STIM_SIZE_PIX	global LENGTH			% total length of line drawingglobal TARGET_SIZE_DEG  % size of targetglobal TARGET_SIZE_PIX% Used in stimulus presentationglobal NSHOW SPACE TRIANGLE PENTA ARC STAR4 ARCRECT STIM_PLACEglobal TX TY T2X T2Yglobal CONFIG NTARGET DIMglobal T_ANGLE T_ECC DONTCLEAR T_ANGLE_OFFSET RED_TARGET COLOR_TARGET