function initStimglobal NSTIM GRID_SIZE_PIX STIM_SIZE_PIX LENGTH CUR_WINDOW SCREEN_RECTglobal STIM_COLORglobal NSHOW SPACE TRIANGLE PENTA ARC STAR4 ARCRECT XGRID YGRID%nshow=4;%calculate the coordinates of stimulirow=2;col=2;SPACE=zeros(row,col);xstart=SCREEN_RECT(3)/2-col*GRID_SIZE_PIX/2;ystart=SCREEN_RECT(4)/2-GRID_SIZE_PIX;for i=1:row	for j=1:col		XGRID((i-1)*col+j)=xstart+(j-1)*GRID_SIZE_PIX;		YGRID((i-1)*col+j)=ystart+(i-1)*GRID_SIZE_PIX;	endend%calculate some shapes%TRIANGLEl=LENGTH/3;s=zeros(3,2);s(2,1)=l;s(3,1)=l/2;s(3,2)=-l*sqrt(3)/2;TRIANGLE=s;TRIANGLE(:,1)=s(:,1)-l/2+GRID_SIZE_PIX/2;TRIANGLE(:,2)=s(:,2)+sqrt(3)*l/6+GRID_SIZE_PIX/2;%PENTAgonl=LENGTH/5;s=zeros(5,2);ang=72*pi/180;s(1,1)=l;s(2,1)=l*cos(ang)+l;s(2,2)=-l*sin(ang);s(3,1)=s(2,1)-l*cos(ang/2);s(3,2)=s(2,2)-l*sin(ang/2);s(4,1)=-l*cos(ang);s(4,2)=s(2,2);PENTA=s;PENTA(:,1)=PENTA(:,1)+(GRID_SIZE_PIX-l)/2;PENTA(:,2)=PENTA(:,2)+GRID_SIZE_PIX/2+l/2*tan(54*pi/180);%ARCr=LENGTH/(2+pi/3);s=zeros(3,2);s(2,1)=-r/2;s(2,2)=-r*sqrt(3)/2;s(3,1)=r/2;s(3,2)=s(2,2);ARC=s;ARC(:,1)=s(:,1)+GRID_SIZE_PIX/2;ARC(:,2)=s(:,2)+sqrt(3)/3*r+GRID_SIZE_PIX/2;ARCRECT=[-r+GRID_SIZE_PIX/2 -r+sqrt(3)/3*r+GRID_SIZE_PIX/2 r+GRID_SIZE_PIX/2 r+sqrt(3)/3*r+GRID_SIZE_PIX/2];%star: 4ang=20*pi/180;l=LENGTH/8;r=l*(sin(ang)+cos(ang));s=zeros(8,2);s(1,2)=-r;s(3,1)=-r;s(5,2)=r;s(7,1)=r;s(2,1)=-l*sin(ang);s(2,2)=s(2,1);s(4,1)=s(2,1);s(4,2)=-s(2,2);s(6,1)=s(4,2);s(6,2)=s(4,2);s(8,1)=s(6,1);s(8,2)=s(2,2);STAR4=s;STAR4(:,1)=s(:,1)+GRID_SIZE_PIX/2;STAR4(:,2)=s(:,2)+GRID_SIZE_PIX/2;