% SCRIPT setGlobalDefaults% Establishes default values for dot variables.% The convention is to use matlab as an environment and to establish all % stimulus variables as global.  That way, the calling program is free to % send matlab commands that set variables or launch functions% make sure the globals are declareddeclareGlobals%COLORS% Adjusted CRED & CGREEN to be equiluminant% (6.7-6.8 on TEK Lumacolor). 09/08/11CRED 	= [255 0 0];CGREEN 	= [0 168 0]; % [0 150 0];CBLUE 	= [0 100 255];%CYELLOW = [177.5 177.5 0]; %[128 128 0];%[255 255 0];CYELLOW = [128 128 0];CWHITE 	= [255 255 255];CGRAY	= [100 100 100]; %[100 100 100];CGRAY2	= [100 100 100];CBLACK 	= [0 0 0];FP_COLOR = CWHITE;FP2_COLOR = CWHITE;STIM_COLOR = CWHITE;T1_COLOR = CGREEN;T2_COLOR = CRED;BG_COLOR = CBLACK;% MONITORCUR_SCREEN 			= 0;	% use main screen (update with acquisition of 2nd board)MON_REFRESH			= 75;	% frames per second% Fixation PointFP_SIZE_DEG = 0.5;CUR_FP = 0;% FP_SIZE_DEG = 0.25; %4.23=5cm% Stimuli parameters (770 specific)NSTIM = 8;GRID_SIZE_DEG = 3;STIM_SIZE_DEG = 1.2;TARGET_SIZE_DEG = 1.2;MONKEY_ID = 1;