function showFP(fpIndex,flip)% This function shows fixation pointglobal CUR_WINDOW FP_SIZE_PIX CENTER_X CENTER_Y FP_COLOR FP2_COLOR CUR_FPif nargin>0	CUR_FP = fpIndex;else	fpIndex = CUR_FP;endif nargin<2    flip = 1;endif (fpIndex)	Screen(CUR_WINDOW,'FillOval',FP2_COLOR,[CENTER_X-FP_SIZE_PIX/2 ...										   CENTER_Y-FP_SIZE_PIX/2 ...										   CENTER_X+FP_SIZE_PIX/2 ...										   CENTER_Y+FP_SIZE_PIX/2]);else   	Screen(CUR_WINDOW,'FillOval',FP_COLOR,[CENTER_X-FP_SIZE_PIX/2 ...									 	    CENTER_Y-FP_SIZE_PIX/2 ...										    CENTER_X+FP_SIZE_PIX/2 ...										    CENTER_Y+FP_SIZE_PIX/2]);endif flip    Screen('Flip',CUR_WINDOW,[],1);end