function showNeutralTarget(ntarget, FPon, config)% This function shows targets% ntarget: number of target% x,y: coordinate of 1st target (2nd target is the 180¡ rotation% fixation: show FP or not% config: specifies choice targetsdeclareGlobals;% global TARGET_SIZE_PIX T1_COLOR T2_COLOR FP_COLOR FP_SIZE_PIX APBG_INDEX CGRAY CRED% global CUR_WINDOW CENTER_X CENTER_Y TTX TTY CONFIG NTARGET DIM% global PIX_PER_DEG MONKEY_IDNTARGET = ntarget;CONFIG = config;e=TARGET_SIZE_PIX/2;if ntarget>2;    for i = 1:ntarget        TTX = round(T_ECC*cos(T_ANGLE(i)));        TTY = round(T_ECC*sin(T_ANGLE(i)));        wx = TTX * PIX_PER_DEG /10 + CENTER_X;        wy = -TTY * PIX_PER_DEG /10 + CENTER_Y;        if config            switch i                case t1                    Screen('FillOval',CUR_WINDOW,T1_COLOR,[wx-e wy-e wx+e wy+e]);                case t2                    Screen('FillOval',CUR_WINDOW,T2_COLOR,[wx-e wy-e wx+e wy+e]);                otherwise                    Screen('FillOval',CUR_WINDOW,CGRAY,[wx-e wy-e wx+e wy+e]);            end        else            Screen('FillOval',CUR_WINDOW,CGRAY,[wx-e wy-e wx+e wy+e]);        end    endendif FPon    % Show FP    Screen('FillOval',CUR_WINDOW,FP_COLOR,[CENTER_X-FP_SIZE_PIX/2 ...                                           CENTER_Y-FP_SIZE_PIX/2 ...                                           CENTER_X+FP_SIZE_PIX/2 ...                                           CENTER_Y+FP_SIZE_PIX/2]);else    Screen('FillRect',CUR_WINDOW,APBG_INDEX,[CENTER_X-FP_SIZE_PIX/2 ...									   CENTER_Y-FP_SIZE_PIX/2 ...									   CENTER_X+FP_SIZE_PIX/2 ...									   CENTER_Y+FP_SIZE_PIX/2]);endScreen('Flip',CUR_WINDOW,0,1);