function showTarget2(ntarget, x, y, x2, y2, choice)% This function shows targets% ntarget: number of target% x,y: coordinate of 1st target (2nd target is the 180¡ rotation% fixation: show FP or notglobal T_RED_INDEX TARGET_SIZE_PIX global CUR_WINDOW CENTER_X CENTER_Yglobal PIX_PER_DEGT1_COLOR = T_RED_INDEX;T2_COLOR = T_RED_INDEX;e=TARGET_SIZE_PIX/2;wx = x * PIX_PER_DEG /10 + CENTER_X;wy = -y * PIX_PER_DEG /10 + CENTER_Y;wx2 = x2 * PIX_PER_DEG /10 + CENTER_X;wy2 = -y2 * PIX_PER_DEG /10 + CENTER_Y;if ntarget==2 | choice==1	Screen('FillOval',CUR_WINDOW,T1_COLOR,[wx-e wy-e wx+e wy+e]);	fprintf('(t1)');endif ntarget==2 | choice==2	Screen('FillOval',CUR_WINDOW,T2_COLOR,[wx2-e wy2-e wx2+e wy2+e]);	fprintf('(t2)');endScreen('Flip',CUR_WINDOW);