function showTarget(ntarget, x, y, x2, y2, choice, FPon, monkey_id)% This function shows targets% ntarget: number of target% x,y: coordinate of 1st target (2nd target is the 180¡ rotation% fixation: show FP or notglobal TARGET_SIZE_PIX T1_COLOR T2_COLOR FP_COLOR FP_SIZE_PIX APBG_INDEXglobal CUR_WINDOW CENTER_X CENTER_Y TX TY T2X T2Yglobal PIX_PER_DEG MONKEY_IDTX = x;TY = y;T2X = x2;T2Y = y2;MONKEY_ID = monkey_id;e=TARGET_SIZE_PIX/2;wx = x * PIX_PER_DEG /10 + CENTER_X;wy = -y * PIX_PER_DEG /10 + CENTER_Y;wx2 = x2 * PIX_PER_DEG /10 + CENTER_X;wy2 = -y2 * PIX_PER_DEG /10 + CENTER_Y;if monkey_id == 1 % two red targets for Eli, green & red for Joey    T1_COLOR = T2_COLOR;endif x==x2 && y==y2    T1_COLOR_ = FP_COLOR;    T2_COLOR_ = FP_COLOR;else    T1_COLOR_ = T1_COLOR;    T2_COLOR_ = T2_COLOR;    endif ntarget==2 || choice==1	Screen('FillOval',CUR_WINDOW,T1_COLOR_,[wx-e wy-e wx+e wy+e]);endif ntarget==2 || choice==2	Screen('FillOval',CUR_WINDOW,T2_COLOR_,[wx2-e wy2-e wx2+e wy2+e]);endif FPon    % Show FP    Screen('FillOval',CUR_WINDOW,FP_COLOR,[CENTER_X-FP_SIZE_PIX/2 ...                                           CENTER_Y-FP_SIZE_PIX/2 ...                                           CENTER_X+FP_SIZE_PIX/2 ...                                           CENTER_Y+FP_SIZE_PIX/2]);else    Screen('FillRect',CUR_WINDOW,APBG_INDEX,[CENTER_X-FP_SIZE_PIX/2 ...									   CENTER_Y-FP_SIZE_PIX/2 ...									   CENTER_X+FP_SIZE_PIX/2 ...									   CENTER_Y+FP_SIZE_PIX/2]);endScreen('Flip',CUR_WINDOW);