function showTargetNoFlip(ntarget, x, y, x2, y2, choice, FPon, config, monkey_id, dim)% This function shows targets% ntarget: number of target% x,y: coordinate of 1st target (2nd target is the 180¡ rotation% fixation: show FP or not% config: specifies choice targetsglobal TARGET_SIZE_PIX T1_COLOR T2_COLOR FP_COLOR FP_SIZE_PIX APBG_INDEX CGRAY CREDglobal CUR_WINDOW CENTER_X CENTER_Y TX TY T2X T2Y TTX TTYglobal PIX_PER_DEG MONKEY_ID CONFIG DIMr = sqrt(x^2+y^2);TX = x;TY = y;T2X = x2;T2Y = y2;MONKEY_ID = monkey_id;e=TARGET_SIZE_PIX/2;if TX>0    theta = atan(TY/TX); % Angle of Target 1else    theta = atan(TY/TX) + pi;endif ntarget>2;    t1 = 1;                 % Target 1 ID    t2 = round(ntarget/2)+1;  % Target 2 ID    for i = 1:ntarget        phi(i) = 2*pi/ntarget*(i-1);        TTX = r*cos(theta+phi(i));        TTY = r*sin(theta+phi(i));        wx = TTX * PIX_PER_DEG /10 + CENTER_X;        wy = -TTY * PIX_PER_DEG /10 + CENTER_Y;        if config            switch i                case t1                    Screen('FillOval',CUR_WINDOW,T1_COLOR,[wx-e wy-e wx+e wy+e]);                case t2                    Screen('FillOval',CUR_WINDOW,T2_COLOR,[wx-e wy-e wx+e wy+e]);                otherwise                    Screen('FillOval',CUR_WINDOW,CGRAY,[wx-e wy-e wx+e wy+e]);            end        else            Screen('FillOval',CUR_WINDOW,CGRAY,[wx-e wy-e wx+e wy+e]);        end    endelse    e=TARGET_SIZE_PIX/2;    wx = x * PIX_PER_DEG /10 + CENTER_X;    wy = -y * PIX_PER_DEG /10 + CENTER_Y;    wx2 = x2 * PIX_PER_DEG /10 + CENTER_X;    wy2 = -y2 * PIX_PER_DEG /10 + CENTER_Y;        % Eli    switch monkey_id;        case 1            T1_COLOR = T2_COLOR;            T1_COLOR_ = T1_COLOR;            T2_COLOR_ = T2_COLOR;            dim = 100;            if ntarget==2                if config                    dim = 100;                else                    dim = DIM;                end            end                    case 2            T1_COLOR_ = T1_COLOR;            T2_COLOR_ = T2_COLOR;            dim = 100;            if ~config                T1_COLOR_ = CGRAY;                T2_COLOR_ = CGRAY;            end    end        if x==x2 && y==y2        T1_COLOR_ = FP_COLOR;        T2_COLOR_ = FP_COLOR;        dim = DIM;    end        if ntarget==2 || choice==1        Screen('FillOval',CUR_WINDOW,T1_COLOR_*dim/100,[wx-e wy-e wx+e wy+e]);    end    if ntarget==2 || choice==2        Screen('FillOval',CUR_WINDOW,T2_COLOR_*dim/100,[wx2-e wy2-e wx2+e wy2+e]);    endendif FPon    % Show FP    Screen('FillOval',CUR_WINDOW,FP_COLOR,[CENTER_X-FP_SIZE_PIX/2 ...                                           CENTER_Y-FP_SIZE_PIX/2 ...                                           CENTER_X+FP_SIZE_PIX/2 ...                                           CENTER_Y+FP_SIZE_PIX/2]);else    Screen('FillRect',CUR_WINDOW,APBG_INDEX,[CENTER_X-FP_SIZE_PIX/2 ...									   CENTER_Y-FP_SIZE_PIX/2 ...									   CENTER_X+FP_SIZE_PIX/2 ...									   CENTER_Y+FP_SIZE_PIX/2]);end